import React from 'react';
import FetchUserData from '../services/Api';

const SearchUser = ({ setUser, display, setDisplay }) => {
  const [username, setUsername] = React.useState('');

  const saveUsername = (event) => {
    setUsername(event.target.value);
  };

  const userInfo = (event) => {
    event.preventDefault();

    const userData = FetchUserData(username);

    userData.then((response) => {
      setUser(response);

      if (response.message === 'Not Found') {
        setDisplay(4);
      } else {
        setDisplay(1);
      }

      console.log(display);
    });

    //console.log(username);
  };

  return (
    <div className="jumbotron jumbotron-custom">
      <div className="title-custom">
        <h1>GitHub Search</h1>
      </div>
      <div className="row">
        <form>
          <div className="form-group">
            <div className="subtitle-custom">
              <label>Username</label>
            </div>
            <input
              type="text"
              className="form-control form-control-custom"
              placeholder="guicardozodasilva"
              onChange={saveUsername}
            />
          </div>
          <button
            type="submit"
            className="btn btn-primary btn-custom"
            onClick={userInfo}
          >
            Search user
          </button>
          <button
            type="submit"
            className="btn btn-primary btn-custom"
            //onClick={this.userReposBtn}
          >
            Repositories
          </button>
          <button
            type="submit"
            className="btn btn-primary btn-custom"
            //onClick={this.userStarredBtn}
          >
            Starred
          </button>
        </form>
      </div>
    </div>
  );
};

export default SearchUser;
